import { range, interval, combineLatest, concat, of } from 'rxjs';
import { map, filter, take, combineAll, concatAll, scan } from 'rxjs/operators';
import { collectionData } from 'rxfire/firestore';
import { tap } from 'rxjs/operators';
import {loadDB } from '../../demo-rx/firebase/firebaseRx';

let  arrayA=["a","b","c"];
const  arrayB=["1","2","3"];
const changestate=()=>{
  interval(3000).pipe(
    map(val=>of(val+1)), concatAll(), take(1)
  ).subscribe(val=>{
    let ars=[`${val}`];
    arrayA=[...arrayA,...ars]
    demoCombineallNumber(arrayA);
  })
}

const manuager = ()=>{
  const db = loadDB();  
  let _strGet=db.firestore().collection('books');

  let vaule= collectionData(_strGet, 'id')
  .pipe(
    tap(cities => console.log('This is just an observable!'))
  );
  return vaule;
  
}

const demoCombineall = ()=>{
  interval(5000).pipe(take(2)).pipe(
    map(val =>
        interval(1000).pipe(
          map(i => `Result (${val}): ${i}`),
          take(5)
        )
  )).pipe(
    combineAll()
  ).subscribe(x => console.log(x));
}

const demoCombineallNumber=(arr=arrayA)=>{
  var result= concat(arr, arrayB).pipe(combineAll());
  
  changestate();
  return result;
}

export {
  manuager,
  demoCombineall,
  demoCombineallNumber
}
